import os filepath=os.path.join( "raw_data_paragraph_1.txt")with open (filepath) as paragraph:     content=paragraph.read()    #print(type(content))    #print (content)        word_count=len(content.split())        sentence_count=len(content.split("."))        characters_no_spaces=len(content)-130        letter_count=round(characters_no_spaces/word_count, 2)        sentence_length=round(word_count/sentence_count, 2)        print("Paragraph Anaylis")    print("-------------------------")    print(f"Approximate Word Count: {word_count}")    print(f"Approximate Sentence Count: {sentence_count}")    print(f"Average Letter Count: {letter_count}")    print(f"Average Sentence Length: {sentence_length}")